import { useEffect, useState } from "react";

export function Random() {
  const [randomAlbums, setRandomAlbums] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/albums")
      .then((response) => response.json())
      .then((data) => setRandomAlbums(data))
      .catch((err) => console.log(err));
  }, []);

  const generateUniqueId = () => {
    return '_' + Math.random().toString(36).substr(2, 9);
  };

  return (
    <div>
      {randomAlbums && randomAlbums.length > 0 ? (
        <div>
          {[...Array(99)].map((_, index) => {
            const randomIndex = Math.floor(Math.random() * randomAlbums.length);
            const album = randomAlbums[randomIndex];
            return (
              <div key={generateUniqueId()}>
                <p>{album.name}</p>
                <img
                  className="cover"
                  src={album.cover_small}
                  alt={`Cover of album ${index + 1}`}
                />
              </div>
            );
          })}
        </div>
      ) : (
        <div className="Chargement">Loading...</div>
      )}
    </div>
  );
}
